UPDATE `npc_text_locale` SET `ID`='12793', `Locale`='esES', `Text0_0`='Hace tiempo caminaba por las Colinas Pardas, pues era hijo del gran Ursoc, quien reinaba en esta tierra.$B$BPero ahora... ahora estoy condenado a pasar el resto de mi vida en esta cueva.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12793') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12794', `Locale`='esES', `Text0_0`='No lo entiendes. Si quisiera, podría salir de esta cueva luchando.$B$BEs mi padre, Ursoc. Ha regresado de entre los muertos... pero ya no es el mismo.$B$BDio su vida durante la Guerra de los Ancestros hace miles de años. Durante siglos, los fúrbolgs que le rendían pleitesía intentaron encontrar un modo de resucitarlo.$B$BFinalmente, decidieron que iban a intentar que el fallido árbol del mundo, Vordrassil, volviera a crecer y usar su magia para resucitar a mi padre.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12794') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12795', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Habla en voz baja, $c. Soy una espía de Rencor Venenoso disfrazada.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12795') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12796', `Locale`='esES', `Text0_0`='El esqueje que crearon tenía un fruto. Al principio pareció funcionar. Ursoc volvió y, durante un breve instante, me llené de alegría.$B$BA medida que se iba recuperando, era evidente que no era el mismo. Tampoco lo eran los fúrbolgs.$B$BEl mal que hizo que los druidas destrozaran a Vordrassil volvió a manifestarse en el nuevo árbol y los convirtió a todos en monstruos sedientos de sangre.$B$BEsa cosa que camina ahora por las Colinas Pardas no es mi padre y ha de ser devuelto a su descanso eterno.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12796') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12797', `Locale`='esES', `Text0_0`='Soy Kodian, hija de Ursoc. Vigilo este santuario que en otro tiempo fue sagrado.$B$BNo he visto a ningún extraño venir aquí desde hace mucho tiempo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12797') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12798', `Locale`='esES', `Text0_0`='Un fúrbolg llamado Tur Patafuria. No es uno de los nuestros.$B$BÉl viene de las tierras del sur. Pertenecía a la tribu Fauces de Madera.$B$BQuería ver a Ursoc. Parecía creer que la corrupción de Vordrassil podía ser purificada y utilizada para salvar a mi padre.$B$BEs una locura albergar tales esperanzas. Estarás de acuerdo conmigo cuando veas la oscuridad que le ha afectado.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12798') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12810', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Saludos, $c.$B$B¿Cómo puedo ayudarte?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12810') AND (`Locale`='esES');
