UPDATE `npc_text_locale` SET `ID`='12823', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='En este baldío helado, estamos aprendiendo rápidamente el significado de la muerte, $N. El poder del Rey Exánime se extiende por cada centímetro de Rasganorte, penetrando en la propia tierra. Él vuelve a nuestros caídos contra nosotros.$B$BDesde el cielo, jinetes de grifos resucitados nos atacan con sus vengativos martillos de tormenta mientras los muertos andantes nos atacan en tierra.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12823') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12824', `Locale`='esES', `Text0_0`='Supongo que Krenna duda de mis habilidades. Mientras nuestros guerreros de élite caminan para enfrentarse a la Alianza y sus aliados tramperos, o se dedican a causar estragos entre los trols, a mí me han ordenado eliminar a esos molestos vrykuls Desuelladragones.$B$BQuizá si unimos nuestros esfuerzos, podamos mostrarle lo que valemos a la conquistadora.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12824') AND (`Locale`='esES');
