UPDATE `npc_text_locale` SET `ID`='12843', `Locale`='esES', `Text0_0`='El alto señor Bolvar Fordragón lucha por todos nosotros. Si no fuera por él, Arthas estaría sentado sobre el trono de Ventormenta en este preciso instante.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12843') AND (`Locale`='esES');
