UPDATE `npc_text_locale` SET `ID`='12848', `Locale`='esES', `Text0_0`='<El sumo abad te estudia un instante y sonríe.>$B$BMuy bien, conozco un lugar, $ghijo:hija;. Tú besa mi anillo y avísame cuando te sientas en condiciones.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12848') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12849', `Locale`='esES', `Text0_0`='Entonces, únete a mí en la entrada de la abadía. Estaré allí en seguida.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12849') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12850', `Locale`='esES', `Text0_0`='He jugado mi papel destruyendo civilizaciones enteras... pero tener que rebajarme a tratar con los desdichados miembros del Culto de los Malditos me repugna.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12850') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12852', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Habla en voz baja. ¡No queremos llamar la atención de estas bestias!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12852') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12853', `Locale`='esES', `Text0_0`='¡No me molestes con nimiedades!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12853') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12856', `Locale`='esES', `Text0_0`='¡No ves que estoy ocupado! ¡Nunca conseguiremos que esta base esté lista cuando regrese el Príncipe!', `Text0_1`=NULL, `Text1_0`='Si esas bestias salvajes no hubieran quemado nuestros barcos, ya estaríamos en casa. ¡Ahora déjame en paz para que pueda seguir trabajando!', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12856') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12857', `Locale`='esES', `Text0_0`='La Cruzada Argenta tiene el control aquí, colega. ¡No íbamos a dejar que te llevaras toda la gloria!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12857') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12858', `Locale`='esES', `Text0_0`='¡Esos malditos campesinos tienen que trabajar más deprisa! Quiero volver a casa, a Forjaz. Echo de menos a mi mujer e hijos.', `Text0_1`=NULL, `Text1_0`='El Capitán dice que en cuanto el príncipe Arthas regrese, volveremos a Lordaeron.', `Text1_1`=NULL, `Text2_0`='No sé por qué está tardando tanto el joven Príncipe, pero no creo que podamos aguantar mucho más. Algunos de los soldados están empezando a notar el frío.', `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12858') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12859', `Locale`='esES', `Text0_0`='Cuando vimos que esas bestias salvajes habían quemado nuestros barcos, nos quedamos con el corazón en un puño. Jamás me había sentido tan indefenso...', `Text0_1`=NULL, `Text1_0`='Rabia. Eso resume mis sentimientos hacia este desastre. Nos dejamos la piel intentando atravesar los bosques y llegar a la orilla, solo para descubrir que era demasiado tarde. Nuestra única forma de volver a casa había sido destruida.', `Text1_1`=NULL, `Text2_0`='A pesar de todos los apuros a los que hemos tenido que hacer frente en estos baldíos, creo que el joven Príncipe nos guiará hasta la victoria. Rezo cada noche para que esté a salvo.', `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12859') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12860', `Locale`='esES', `Text0_0`='<El lacayo olvidado agita la cabeza.>$B$BNo podemos continuar así, colega. Si el príncipe Arthas no regresa pronto, moriremos todos.', `Text0_1`=NULL, `Text1_0`='Nunca tendremos estos barcos listos a tiempo. El invierno se acerca y con él nuestra perdición. Temo que el joven Príncipe nos ha condenado a todos.', `Text1_1`=NULL, `Text2_0`='Hace mucho que Arthas y Muradin se han ido. Espero que no estén en peligro.', `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12860') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12862', `Locale`='esES', `Text0_0`='¿Dónde puedo enviarte?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12862') AND (`Locale`='esES');
