UPDATE `npc_text_locale` SET `ID`='12949', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='El Vuelo Infinito está tratando de alterar el destino del príncipe Arthas Menethil. Ahora lo conoces como el Rey Exánime, pero aquí y en este momento aún era un príncipe de Lordaeron que trataba de hacer lo que era mejor para su reino. Hoy Arthas toma una decisión que marcará el destino, llevando a lo que los Vigilantes del Tiempo llamamos un punto de inflexión en la línea temporal.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12949') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12950', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Arthas ordenó arrasar Stratholme, matando a todos los humanos que hubiera dentro de sus murallas. Descubrió pruebas de que la Plaga había infectado la ciudad con su insidiosa peste y la enfermedad no solo mata a la gente. Los que mueren debido a esta peste se vuelven a despertar convertidos en zombis descerebrados que alimentan la máquina de guerra de la Plaga.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12950') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='12952', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Los Infinitos están intentando algo con una perspicacia poco común en ellos. Están tratando de ocultar las pruebas que llevarán a Arthas a tomar la decisión de arrasar Stratholme. Sus agentes han usado magia ilusionista para que los cargamentos de grano de los campos cercanos parezcan normales. Necesito que encuentres el grano y uses este disruptor Arcano en los envíos. Los hombres de Arthas están buscando grano apestado y lo encontrarán rápidamente cuando desaparezca la ilusión.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='12952') AND (`Locale`='esES');
