UPDATE `npc_text_locale` SET `ID`='13180', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Zim\'Torga puede proporcionarnos cierto nivel de protección, pero la presencia de la Plaga y las acciones de los Drakkari la han debilitado mucho.$B$BAhunae y yo nos ocuparemos del resto de nuestras defensas.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13180') AND (`Locale`='esES');
