UPDATE `npc_text_locale` SET `ID`='13237', `Locale`='esES', `Text0_0`='El tramposo Señor de la Magia Urom protege el tercer anillo. Aparecerá solo e indefenso, ¡pero que no te engañen las apariencias! Urom es un poderoso conjurador que dirige un grupo de criaturas fantasmales. Búscale arriba.$BPuedo otorgarte el poder de invocar a un draco del Vuelo Rojo. Habla con Eternos o Verdisa si prefieres aprovechar el poder del Bronce o del Verde.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13237') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='13238', `Locale`='esES', `Text0_0`='El mayor de tus desafíos aún está por llegar. El Guardián-Ley Eregos es un dragón azul de inmenso poder. Lo encontrarás sobrevolando el anillo superior.$B$BSe ha desbloqueado el poder total de tus dracos. ¡Usa su poder para derrotar a Eregos!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13238') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='13254', `Locale`='esES', `Text0_0`='Los dracos rubí destacan en aguantar daño y proteger a sus aliados.$B$BAl utilizar Cólera abrasadora pueden lanzar fuego líquido que salta de objetivo en objetivo, haciendo cada vez más daño.$B$BCada vez que un draco rubí es alcanzado por un ataque enemigo, gana una Carga evasiva. Estas cargas sirven para realizar una serie de maniobras evasivas, que permiten al draco esquivar los ataques y hechizos durante un tiempo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13254') AND (`Locale`='esES');
