UPDATE `npc_text_locale` SET `ID`='13300', `Locale`='esES', `Text0_0`='Oooh... midda al pequeño necrófago.$B$B¡Zalta, pequeñño! ¡Zalta!$B$BJua, jua jua... Buen pequeñño necrófago.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13300') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='13303', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='La Plaga es una mancha en estas prístinas tierras. Por el poder de los creadores, aplastaré hasta el último de sus decrépitos huesos bajo mi talón.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13303') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='13304', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Los titanes no crearon la vida de la noche a la mañana. El proceso fue gradual y necesitó muchos experimentos e iteración.$B$BPor eso existen lugares tan protegidos como la Cuenca de Sholazar.$B$BPara mantener la seguridad de sus lugares de experimentación, los titanes crearon un mecanismo de defensa. Su fuerza destructiva no tiene igual y no te contaré nada más salvo que jures lealtad a mi causa.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13304') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='13305', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Revelar estos secretos a un mortal como tú roza el sacrilegio, pero no tengo elección.$B$BDesde que Freya, hija de los titanes, me dejó aquí, solo he tenido una tarea en la que no puedo fracasar. La vida ha de ser protegida cueste lo que cueste.$B$BHay una puerta que une este lugar con otro muy parecido. Allí es donde se encuentra el arma que buscamos.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13305') AND (`Locale`='esES');
