UPDATE `npc_text_locale` SET `ID`='13511', `Locale`='esES', `Text0_0`='¡$N! Apenas te reconozco con esas ropas nuevas. ¿Todavía tienes esa túnica... o escogiste las albarcas... o fue el bastón? No lo recuerdo, fue hace tanto tiempo...$B$BBueno, me alegro de que estés aquí para ayudar de nuevo. Necesitamos toda la ayuda posible. $N, si actúas tal y como lo hiciste allá en los Páramos de Poniente con la Milicia del Pueblo, tendremos una oportunidad.$B$BEstos son tiempos duros y si no ganamos esta batalla, puede que no haya unos Páramos de Poniente a los que volver.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13511') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='13512', `Locale`='esES', `Text0_0`='Con la bendición de la Luz y tu ayuda, $N, los Defias dejaron de ser una amenaza.$B$BPero ahora estamos muy lejos de los Páramos de Poniente y si no ganamos esta batalla, puede que no tengamos un hogar al que volver.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13512') AND (`Locale`='esES');
