UPDATE `npc_text_locale` SET `ID`='13614', `Locale`='esES', `Text0_0`='Es un poco tarde... para eso... $gmuchacho:muchacha;.$B$B<El explorador Natoescarcha tose algo de sangre sobre la nieve.>$B$BCoge... al joven que está al mando por mí.$B$B<El explorador Natoescarcha se desploma mientras exhala su último aliento.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13614') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='13615', `Locale`='esES', `Text0_0`='Mira a tu alrededor y deja que la devastación cause su efecto, $c. Hasta el último trol que vivía aquí está muerto o ha huido a los niveles superiores.$B$BY solo empeora.$B$BEn un acto de desesperación, los trols Drakkari vivos sacrificaron a sus propios dioses animales para poder beber la sangre de los dioses y conseguir un poder increíble. Aunque tuvieron éxito al mantener a raya a la Plaga con su nueva fuerza, los resultados fueron catastróficos. La prueba es que el antiguo imperio de Zul\'Drak yace en ruinas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13615') AND (`Locale`='esES');
