UPDATE `npc_text_locale` SET `ID`='13654', `Locale`='esES', `Text0_0`='Debemos atacar Jotunheim continuamente. ¡No darles ninguna oportunidad de recobrar el aliento, ni de dormir, ni de aceptar la derrota!$B$BTirion Vadín no podrá conseguir lo que estás haciendo ni con todo el poder de la Crematoria, ni con todo el apoyo de la Cruzada Argenta.$B$BAhora, date prisa con lo tuyo, $N. Están ocurriendo demasiadas cosas como para pararse a charlar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='13654') AND (`Locale`='esES');
