UPDATE `npc_text_locale` SET `ID`='14364', `Locale`='esES', `Text0_0`='Puede que estas preciosidades parezcan chatarra, pero pueden embestir a los enemigos. El espolón central ha sido sobrecargado con un híper condensador electroscópico que le permite propinar descargas eléctricas. $B$BIncluso la torreta ha sido mejorada con cohetes antiaéreos, algo que proporciona, definitivamente, una ventaja sobre esos malditos demoledores.', `Text0_1`='Puede que estas preciosidades parezcan chatarra, pero pueden embestir a los enemigos. El espolón central ha sido sobrecargado con un híper condensador electroscópico que le permite propinar descargas eléctricas. $B$BIncluso la torreta ha sido mejorada con cohetes antiaéreos, algo que proporciona, definitivamente, una ventaja sobre esos malditos demoledores.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14364') AND (`Locale`='esES');
