UPDATE `npc_text_locale` SET `ID`='14368', `Locale`='esES', `Text0_0`='No hay nada que escupa fuego mejor que estas preciosidades. Incluso hemos conectado un cañón secundario y un gancho prensil, y hemos añadido un sistema manual de inyección de combustible. Si puedes obtener pirita líquida de los mecagnomos, puedes usarla para arrojar explosivos de alta densidad, o para incrementar tu velocidad de movimiento.$B$B¡A ver si esos mequetrefes de la Liga de Expedicionarios pueden hacer algo mejor!', `Text0_1`='No hay nada que escupa fuego mejor que estas preciosidades. Incluso hemos conectado un cañón secundario y un gancho prensil, y hemos añadido un sistema manual de inyección de combustible. Si puedes obtener pirita líquida de los mecagnomos, puedes usarla para arrojar explosivos de alta densidad, o para incrementar tu velocidad de movimiento.$B$B¡A ver si esos mequetrefes de la Liga de Expedicionarios pueden hacer algo mejor!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14368') AND (`Locale`='esES');
