UPDATE `npc_text_locale` SET `ID`='14648', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='En cada mesa generosa se sirven cinco comidas de la Generosidad del Peregrino, una delante de cada silla.$B$BMientras estés en una de esta sillas, podrás comer tanto como quieras, además de pasar raciones de esa comida a las demás sillas. ¡Come y comparte con los amigos!$B$BSi comes suficiente de una comida, obtendrás beneficios únicos de esa comida.$B$B¡Además, si comes suficiente de cada comida, obtendrás El espíritu de compartir!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14648') AND (`Locale`='esES');
