UPDATE `npc_text_locale` SET `ID`='14681', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Eh, cariño! ¿Qué tal estoy?', `Text1_0`=NULL, `Text1_1`='¡Te he echado tanto de menos! ¿Dónde has estado?$B$B¿Vas a contestarme o simplemente te vas a quedar ahí de pie?', `Text2_0`=NULL, `Text2_1`='¿Te gusta mi modelito? Es para la fiesta. Lo he cargado a tu cuenta.', `Text3_0`=NULL, `Text3_1`='Había un tío que estaba tirándome los tejos. ¡Qué sinvergüenza!$B$B¿Irás a darle una paliza por mí, cariñín? Gracias. ¡Te quiero!', `Text4_0`=NULL, `Text4_1`='No puedo esperar a que llegue el día en el que estés en lo más alto. Piénsalo, podré hacerme llamar Señora de Príncipe mercante.$B$B¡¡Y podré comprarme todo lo que siempre he querido, o sea!!', `Text5_0`=NULL, `Text5_1`='Ten cuidado ahí fuera. He oído que hay Piratas de los Mares del Sur en la bahía.$B$BMmm, quizá deba ir a comprobarlo yo misma...', `Text6_0`=NULL, `Text6_1`='¿Podemos ir a hacer un picnic romántico en la cumbre del Monte Kajaro?$B$B¡Asegúrate de coger mi champán favorito, o sea!', `Text7_0`=NULL, `Text7_1`='Tienes esa mirada traviesa en los ojos. ¿Qué has estado haciendo, cielo?$B$B¿Y bien?' WHERE (`ID`='14681') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14682', `Locale`='esES', `Text0_0`='Eso es genial, tan solo hazme una señal para que detenga los hechizos que lo retienen.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14682') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14683', `Locale`='esES', `Text0_0`='¡Te adoro! Me crees, ¿verdad, cariño?', `Text0_1`=NULL, `Text1_0`='Cariño, ¿dónde has estado? ¡Ya sabes lo preocupado que me quedo cuando no me dices dónde andas!', `Text1_1`=NULL, `Text2_0`='Cariño, ¿qué opinas de mis abdominales? Acabo de salir del gimnasio.$B$B¡Soy un pedazo de carne bastante potable!', `Text2_1`=NULL, `Text3_0`='Esas mujeres son solo amigas, cariño.', `Text3_1`=NULL, `Text4_0`='¡Me gusta eso que llevas puesto!', `Text4_1`=NULL, `Text5_0`='Sé que te lo prometí ayer por la noche, y la noche anterior, ¡pero ahora te prometo que te llevaré a un sitio muy bonito mañana por la noche!', `Text5_1`=NULL, `Text6_0`='He oído que algunos Piratas de los Mares del Sur han llegado a puerto hace poco. ¡Ten cuidado si vas a la ciudad!$B$B¡Incluso tienen mujeres pirata! Mmm, ¿quizá deba ir a comprobarlo yo mismo? Solo para asegurarme de que ahí fuera no hay peligro para ti, cariño.', `Text6_1`=NULL, `Text7_0`='¿Qué tal está hoy mi novia maciza directiva de la empresa? ¡En vista de cómo llevas las cosas por aquí, creo que cualquier día tendremos que llamarte Princesa mercante $N!$B$B¡No puedo esperar! Voy a pasar los días haciendo ejercicio en el gimnasio y trabajándome el moreno en la piscina.', `Text7_1`=NULL WHERE (`ID`='14683') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14684', `Locale`='esES', `Text0_0`='¡Hola $gjefe:jefa;! ¡Este envío de esclavos trols es el peor que hemos recibido hasta ahora!$B$BSuerte que la kaja\'mita no ha estimulado sus blandos cerebros para hacerlos más inteligentes, ¿eh?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14684') AND (`Locale`='esES');
