UPDATE `npc_text_locale` SET `ID`='14689', `Locale`='esES', `Text0_0`='¿Vienes para aprender a montar?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14689') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14690', `Locale`='esES', `Text0_0`='Durante años he servido en este rincón remoto y olvidado del mundo, memorizando el terreno y esperando mi momento. Y por fin ha llegado. Todo el poder de Orgrimmar está concentrado ahora en Azshara, nuestra puerta de entrada a Vallefresno y al dominio de todo el continente. Te doy la bienvenida al frente, $c.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14690') AND (`Locale`='esES');
