UPDATE `npc_text_locale` SET `ID`='14694', `Locale`='esES', `Text0_0`='¡Lárgate, colega, antes de que lance mi clásico puñetazo Locko Mia directo a tu cara!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14694') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14695', `Locale`='esES', `Text0_0`='Te doy la bienvenida al Primer Banco de Kezan, una filial propiedad integral del Banco Pantoque de Comerciantes y Mercaderes de los Mares del Sur.$B$B¿Qué puedo hacer por ti hoy?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14695') AND (`Locale`='esES');
