UPDATE `npc_text_locale` SET `ID`='14700', `Locale`='esES', `Text0_0`='Mira con qué tengo que trabajar. ¿Szabo sabe hacer milagros?$B$B<Szabo suspira.>$B$BSí, sí. Szabo cree que puede hacer algo contigo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14700') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14702', `Locale`='esES', `Text0_0`='Oh, mira lo que ha hecho Szabo. ¡Una obra de arte!$B$BTú no eres lo suficientemente importante como para llevarlo, pero Szabo necesita el dinero. <Suspiro.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14702') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14703', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Tengo todas las marcas de gafas de sol! ¡Dime cuál es la tuya y te juro que seguro que la tengo, o sea!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14703') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14705', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Aquí tienes. Mi mejor par ¡y te las dejo a precio de ganga!$B$B¡Vas a tener una pinta súper molona con ellas puestas, o sea!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14705') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14712', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Bienvenid$go:a; a nuestra tienda. Tenemos todo lo que necesitas... si necesitas suministros de alquimia.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14712') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14713', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No, yo soy la intelectual y no hay que tomarme a la ligera. Podría hacer una poción sanadora con agua de fregar y dientes de león, pero no es algo que vaya a mostrarte.$B$BDe todas formas, si quieres comprar algo, habla con Patricia.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14713') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14723', `Locale`='esES', `Text0_0`='Los espíritus de los elementos son sabios. Estaban aquí antes que nosotros y continuarán mucho después de que nos hayamos ido. Cuando nos hablan, debemos escuchar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14723') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14724', `Locale`='esES', `Text0_0`='Los espíritus de los elementos nos rodean. Debemos escuchar siempre. He visto de primera mano las consecuencias de ignorar sus avisos...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14724') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14731', `Locale`='esES', `Text0_0`='Gracias de nuevo por ayudarme con esos vagos cretinos $gjefe:jefa;.$B$BSi quieres saber mi opinión, no debimos ser tan indulgentes con los trols cuando conseguimos liberarnos de la esclavitud a la que nos tenían sometidos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14731') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14736', `Locale`='esES', `Text0_0`='Hay quienes desean dejar de ganar experiencia con sus acciones, mantenerse por siempre en el nivel de fuerza en el que se encuentran ahora y no volverse más fuertes.$B$BPuedo hacerlo posible... pero tiene un precio, por supuesto.$B$BPero que quede claro: cuando me pagues, ya NO GANARÁS EXPERIENCIA. Nada. Ni un poquito.$B$BSi cambias de idea después y quieres volver a ganar experiencia de nuevo, puedo deshacer el proceso por el mismo precio.', `Text0_1`='Hay quienes desean dejar de ganar experiencia con sus acciones, mantenerse por siempre en el nivel de fuerza en el que se encuentran ahora y no volverse más fuertes.$B$BPuedo hacerlo posible... pero tiene un precio, por supuesto.$B$BPero que quede claro: cuando me pagues, ya NO GANARÁS EXPERIENCIA. Nada. Ni un poquito.$B$BSi cambias de idea después y quieres volver a ganar experiencia de nuevo, puedo deshacer el proceso por el mismo precio.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14736') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14737', `Locale`='esES', `Text0_0`='Ha sido una demostración impresionante. Pero, ¿estás $gpreparado:preparada; para el siguiente reto?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14737') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14738', `Locale`='esES', `Text0_0`='Hoy has impresionado a todo el mundo con tus habilidades en la arena. Ya es hora de que todos te conozcan como $gel campeón:la campeona; del torneo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14738') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14739', `Locale`='esES', `Text0_0`='Este monumento cruje y emite un zumbido debido a perpetuas y ancestrales energías.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14739') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14740', `Locale`='esES', `Text0_0`='Estás observando lo último de lo último en operaciones mineras en todo el mundo. Tan solo un paleto se molestaría en excavar un túnel para llegar al mineral que se encuentra debajo. ¡Hemos movido la montaña entera! Ahora solo tenemos que extraer el mineral de la superficie de la tierra.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14740') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14742', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Después de tanto tiempo por fin tenemos una oportunidad de estudiar las ruinas salpicadas por este paisaje... Siempre y cuando esos avariciosos no hayan arrasado ya este lugar.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14742') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14744', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Has oído hablar de la guerra de los Ancestros, $N? Así es como llamamos a ese período de tiempo de hace 10.000 años, cuando los demonios de la Legión Ardiente entraron por primera vez en el reino de Azeroth e intentaron devorar todo el mundo, porque tenían muchos más. La antigua capital de Azshara, una ciudad que en otros tiempos dominaba este paisaje, fue el lugar donde comenzó la invasión. Los restos antiguos que encontrarás desperdigados por toda esta zona son el testimonio de la presunción de nuestros antepasados.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14744') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14745', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Mi preocupación principal ahora mismo son las ruinas que rodean el lago Mennar, al este. Hace miles de años la Academia Mennar fue una de las primeras en estudiar las artes arcanas. Era un edificio construido en terrazas, muy hermoso, que se alzaba en forma de anillo alrededor del tranquilo lago. Fue allí donde mis antepasados descubrieron el potencial de las energías mágicas que se filtraban del Pozo de la Eternidad.$B$BLo siento, ¿es demasiada información de una vez? Llevo varias décadas inmersa en la historia de lo arcano y olvido que no todo el mundo comparte mi pasión.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14745') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14746', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No hace falta decir que la academia era muy cuidadosa con la aplicación de esos poderes recién descubiertos. Los pocos escritos que nos han llegado indican que los magos estaban preocupados por la adicción a lo arcano, un problema que mi raza sigue sufriendo incluso hoy en día.$B$BEsa limitación autoimpuesta puso a los magos de Mennar en contra de la reina Azshara y sus lacayos Altonato, que buscaban mayores poderes sin preocuparse por nada más, sin tomar precauciones y desarrollando incluso aplicaciones más peligrosas para las artes.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14746') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14747', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Cuando la Legión Ardiente comenzó su ataque, los magos de la Academia Mennar estaban lejos de gozar del beneplácito de la reina. Habrían sido una poderosa ayuda en la guerra que estaba a punto de desatarse, pero se negaron a retirarse cuando el resto de la ciudad cayó.$B$BEn vez de reagruparse en el exterior de la ciudad para unirse al contraataque, se quedaron atrás para levantar barreras mágicas y convertir su tranquilo hogar en una fortaleza. Los refugiados acudieron en masa a la Academia Mennar en los primeros terribles días de la invasión, y los supervivientes se agruparon para montar una defensa.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14747') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14748', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Aguantar? ¿Contra un ataque frontal de la vanguardia de la Legión? ¡Desde luego que no!$B$BNo, la historia no recoge lo que ocurrió después... porque no hubo supervivientes. Los demonios furiosos no dejaron más que ruinas que puedes ver, y los secretos de la academia yacen enterrados bajo la tierra de Azshara.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14748') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14749', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Me gusta tu forma de pensar, $r!$B$BHasta hace poco, las energías mágicas latentes de la tierra donde se alzaba la academia atraían la atención del Vuelo Azul y me resultaba imposible estudiarlas. Ahora la atención de los dragones está en otra parte, pero una amenaza mayor se cierne sobre el lugar... ¡Esas pequeñas bestias verdes quieren arrasar las ruinas para abrir el camino para construir un depósito de combustible! El conocimiento perdido hace diez mil años está a unos pocos días de perderse para siempre. Tenemos que actuar rápido.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14749') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='14751', `Locale`='esES', `Text0_0`='El viento corta esta cima despiadadamente y la vida ha huido de este rincón de Corona de Hielo. Sin embargo, solo hará falta un poco de coacción para que regrese y por eso camino por estas tierras. Algún día, estos árboles se alzarán sobre nosotros.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='14751') AND (`Locale`='esES');
