UPDATE `npc_text_locale` SET `ID`='15159', `Locale`='esES', `Text0_0`='El Veredicto Cinéreo es un grupo selecto de los artesanos con más talento de la Espada de Ébano y la Cruzada Argenta. Su creación simboliza la unión de nuestras fuerzas a medida que ascendemos hacia el asalto final contra el Rey Exánime y sus esbirros.$B$BEs gracias al esfuerzo del Veredicto Cinéreo que hemos descubierto como amoldar la saronita primordial a nuestra voluntad.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15159') AND (`Locale`='esES');
