UPDATE `npc_text_locale` SET `ID`='15212', `Locale`='esES', `Text0_0`='¿Has oído hablar de nuestro programa \"Talismanes caritativos\"?$B$BLas mascotas oficiales de eventos de La Corona, S.L. como yo podemos distribuir Juegos de coleccionista de talismanes preciosos.$B$BPuedes usar los talismanes preciosos para crear pulseras de talismanes preciosos para tus amigos y amados, o entregárselas a mercaderes preciosas a cambio de pruebas de amor.$B$BUna actividad popular es crear pulseras para tus líderes preferidos para que sepan que se les valora y adora...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15212') AND (`Locale`='esES');
