UPDATE `npc_text_locale` SET `ID`='15219', `Locale`='esES', `Text0_0`='He oído rumores de que mi hijo está aquí. Que ahora es un poderoso caballero de la Muerte en servicio del Rey Exánime. Si es cierto, la línea de descendencia acaba en mí.$B$BMi tarea ahora es dirigir el Martillo de Orgrim a la Ciudadela de la Corona de Hielo y destruir toda la Plaga en el camino. Partimos cuando tus aliados y tú estéis listos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15219') AND (`Locale`='esES');
