UPDATE `npc_text_locale` SET `ID`='15243', `Locale`='esES', `Text0_0`='¡Mi genio no se puede contener en una caja!$B$B¡¿Espera, esto no es Azshara? ¿Qué es este lugar? ¡Un volcán inactivo!$B$B¡La mitad de mi mente quiere que vaya a unirme al Príncipe mercante! ¡La otra mitad está considerando convertir en armas los huevos de la cloqueadora!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15243') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15244', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Cuando nos metieron en la Ciudad de Bolsillo, nos dijeron que nos reasentarían en alguna parte de Azshara. ¿Esto es Azshara?$B$BY yo me pregunto: ¿a quién se le ocurre preparar una Ciudad de Bolsillo sin proveer la comida suficiente para vivir? ¡Un mísero Pollotizador!$B$BTodos sabemos cómo resultó el \"micro\" mecapollo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15244') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15246', `Locale`='esES', `Text0_0`='Arrodíllate junto a mí, colega. Me dio una bala durante el último encuentro.$B$B<Gann gime de dolor mientras trata de cambiar de posición. Su mano izquierda aprieta una herida y la sangre se amontona entre sus temblorosos dedos.>$B$BLa primera vez que los enanos invadieron Los Baldíos, me contenté con hostigar a sus excavadores y sabotear su equipo. Debería haber asestado un golpe mortal cuando tuve la oportunidad.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15246') AND (`Locale`='esES');
