UPDATE `npc_text_locale` SET `ID`='15482', `Locale`='esES', `Text0_0`='Todavía estamos aislados del Mando de Vanguardia, pero gracias a tus \"negociaciones\", con un poco de trabajo duro debería ser posible reabrir los caminos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15482') AND (`Locale`='esES');
