UPDATE `npc_text_locale` SET `ID`='15495', `Locale`='esES', `Text0_0`='¿Cómo te llamas, $c? ¿$N?$B$BTe doy la bienvenida a Los Baldíos. Estás en la vanguardia de una de las mayores operaciones del continente. Me sentiría muy honrado si te quedaras para prestarnos tu fuerza.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15495') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15496', `Locale`='esES', `Text0_0`='Quiero que sepas que aprecio tu ayuda de verdad, $N. No estamos luchando contra animales, sino que nos enfrentamos a un enemigo astuto y fanático. Debemos permanecer juntos o separarnos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15496') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15500', `Locale`='esES', `Text0_0`='¡Eh\'to es muy gordo! Vol\'jin no se anda con tontería\'. Va detrás de esa Bruja del Mar pa\' cobrarse una venganza terrible.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15500') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15501', `Locale`='esES', `Text0_0`='Eh, $gtío:tía;, tan solo estoy intentando llevarme mi trozo de pastel.', `Text0_1`='Eh, $gtío:tía;, tan solo estoy intentando llevarme mi trozo de pastel.', `Text1_0`='¿Quieres tajada? ¡Ven a por ella, colega!', `Text1_1`='¿Quieres tajada? ¡Ven a por ella, colega!', `Text2_0`='¡Rápido, $c! Coge todo lo que puedas.', `Text2_1`='¡Rápido, $c! Coge todo lo que puedas.', `Text3_0`='Estos tauren muertos no tienen más que cuentas y fruslerías. ¿Has conseguido algo bueno?', `Text3_1`='Estos tauren muertos no tienen más que cuentas y fruslerías. ¿Has conseguido algo bueno?', `Text4_0`='¡Oye, $c! Este lugar está bastante saqueado, pero a lo mejor aún puedes encontrar algo que merezca la pena.', `Text4_1`='¡Oye, $c! Este lugar está bastante saqueado, pero a lo mejor aún puedes encontrar algo que merezca la pena.', `Text5_0`='¡Oye! ¿No habrás venido a liberarnos?', `Text5_1`='¡Oye! ¿No habrás venido a liberarnos?', `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15501') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15508', `Locale`='esES', `Text0_0`='El príncipe mercante Gallywix tomó prisioneros a todos tus amigos mientras el volcán entraba en erupción.$B$BVamos a tener que encargarnos de la Alianza muy rápido si queremos rescatarlos y salir de esta isla.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15508') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15510', `Locale`='esES', `Text0_0`='Trabajar para Jerrod no es tan malo. A veces pierde la compostura, pero paga bien y hasta me aúpa cuando no llego a coger algo del carro.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15510') AND (`Locale`='esES');
