UPDATE `npc_text_locale` SET `ID`='15602', `Locale`='esES', `Text0_0`='El continuo sacrificio del Veredicto Cinéreo y de los héroes como tú nos acerca a todos a la victoria final. He venido a la Ciudadela de la Corona de Hielo para prestar mi ayuda a aquellos que luchan contra la Plaga. ¡Juntos acabaremos con la tiranía del Rey Exánime!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15602') AND (`Locale`='esES');
