UPDATE `npc_text_locale` SET `ID`='15633', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Has venido a ayudar, $c? Este valle es como un cuello de botella natural. Si podemos estrangular la línea de suministros, ganaremos tiempo para que nuestros compañeros del Árbol del Mundo monten un contraataque.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15633') AND (`Locale`='esES');
