UPDATE `npc_text_locale` SET `ID`='15704', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Claro! ¡Sí que te había visto aquí antes! ¿Cómo he podido olvidarlo? Puedo teletransportarte a Stratholme ahora si quieres.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15704') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15711', `Locale`='esES', `Text0_0`='¡Thoth\'al amun Ree\'thael vormos! ¡Vormos! ¡Vormos!', `Text0_1`=NULL, `Text1_0`='Al\'tha, Al\'tha bormaz. ¿Ni bormaz ta\'thall? ¡Se acerca, camina en las llamas, $r!', `Text1_1`=NULL, `Text2_0`='El sombra ve. Bo\'al lal arwi C\'toth. El fin se acerca, cubierto de fuego silencioso.', `Text2_1`=NULL, `Text3_0`='Mar\'kowa tallol ye\'tarin, lo sabe, lo sabe.', `Text3_1`=NULL, `Text4_0`='El temor se abrasa en el corazón del infinito. ¡No me obligues a mirar! ¡He visto más de lo que puedo soportar!', `Text4_1`=NULL, `Text5_0`='No somos nada. Tulall par\'okoth. Far\'al, ka\'kar. El vacío devora.', `Text5_1`=NULL, `Text6_0`='<El servidor Crepuscular balbucea y de su boca mana espuma blanca.>', `Text6_1`=NULL, `Text7_0`='H\'thon, la columna de oscuridad que se extiende más allá de donde alcanza la vista. H\'thon marwol qualar: la comprensión infinita de la noche.', `Text7_1`=NULL WHERE (`ID`='15711') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15712', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='$N, los esclavos Crepusculares que tú y otros habéis estado rescatando de la Senda del Lobo, están vagando por el campamento. Pero no pueden decirme quiénes son, hablan como en acertijos y no dicen más que tonterías.$B$BYo creía que el Martillo Crepuscular mantenía a los esclavos a raya al viejo estilo, ya sabes, látigos y cadenas, pero está claro que les han sometido también a algún tipo de lavado de cerebro...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15712') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15714', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Has tenido suerte? El mero intento de mantener una conversación con los esclavos liberados hace que me duela la cabeza.$B$BEstán justo en la frontera entre la discordancia y la rima.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15714') AND (`Locale`='esES');
