UPDATE `npc_text_locale` SET `ID`='15725', `Locale`='esES', `Text0_0`='Hola, $c. Supongo que tienes un montón de preguntas.$B$BBasta con que te diga que Royce Susurro del Anochecer me envió al corazón de una cueva llena de ogros en busca de algo llamado \"Ojo del Crepúsculo\", un trabajo fácil que no ha ido muy bien.$B$BSi alguna vez le pongo las manos encima a ese pequeño elfo de la noche desgarbado, cruel y de palabras comedidas...$B$B<Kristoff se retuerce mientras cuelga en el aire, con el rostro sonrojado.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15725') AND (`Locale`='esES');
