UPDATE `npc_text_locale` SET `ID`='15742', `Locale`='esES', `Text0_0`='De todos los ancestros, Goldrinn es el más magnífico. Su furia, su determinación... Harías bien en imitarlas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15742') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15756', `Locale`='esES', `Text0_0`='Era un buen hombre, $r. Luchamos juntos en muchas batallas...$B$B¡Esas bestias deben pagar!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15756') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15760', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Nuestras plegarias a Aessina no reciben respuesta. Los esbirros ardientes de Ragnaros nos han llevado al límite. Si no los detenemos aquí, ¡el Árbol del Mundo no será más que un tocón envuelto en llamas!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15760') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15769', `Locale`='esES', `Text0_0`='Ugh...$B$BNo me encuentro... muy bien...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15769') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15770', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Gracias, $c.$B$BCasi muero ahí fuera.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15770') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15772', `Locale`='esES', `Text0_0`='$R. ¿Te han enviado para que hables conmigo? ¿Cuánto tiempo ha pasado desde la muerte del maestro en estas mismas laderas?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15772') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15774', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Te doy la bienvenida, $c.$B$BHyjal arde y el mundo que he protegido toda mi vida está al borde del olvido.$B$BDime, ¿has oído hablar de Tyrus Cuerno Negro?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15774') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15778', `Locale`='esES', `Text0_0`='¿Mi ayuda? ¿Las cosas se han puesto tan difíciles?$B$B<Una llama lejana arde en los ojos oscuros y crueles de Cuerno Negro>.$B$BDime por qué no debería dejar que ardiera el mundo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15778') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15780', `Locale`='esES', `Text0_0`='¿Mortal? Sí, una vez fui mortal. Débil.$B$B¿Ves estas ataduras? Esto no es nada en comparación con ser confinado dentro de una forma mortal.$B$BIncluso ahora los dones de mi amo se agitan en mi interior, listos para ser liberados.$B$BNo, $c, mis días de mortal han pasado ya, y si todavía hay una chispa de mi antigua vida en mi interior, ojalá pudiera extinguirla. Tendrás que esforzarte un poco más, $r.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15780') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15781', `Locale`='esES', `Text0_0`='¿Destruido? Sí, eso es posible. Por las noches me asaltan visiones en las que este mundo es desgarrado desde el interior. No siento pena ni rabia. Simplemente... curiosidad.$B$BServí a mi señor a cambio de poder, $c. Pero aquellos que luchan... están condenados al olvido. Es una motivación muy extraña.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15781') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15782', `Locale`='esES', `Text0_0`='<Cuerno Negro tira de sus ataduras, por primera vez visiblemente afectado.>$B$B¡Redención! ¡Actúas como si hubiera alguna posibilidad de escapar de la Legión Ardiente! ¡Eres tan engreído! Como si tus esfuerzos de hace una década o en Terrallende hubieran retrasado a la Legión un solo ápice! Vendrán $r. Regresarán. Y yo estaré aquí para darles la bienvenida.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15782') AND (`Locale`='esES');
