UPDATE `npc_text_locale` SET `ID`='15794', `Locale`='esES', `Text0_0`='¡Estupendo! ¡Has sabido cómo llegar aquí!$B$BNo tenemos tiempo que perder. ¿$gListo:Lista;?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15794') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15802', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Yo me había imaginado que mi primer viaje por el océano sería más pacífico e iluminador. ¿Quizá creía que podría llegar a fundirme con la naturaleza?$B$BPero en este lugar hay muy poca naturaleza. Los elementos que nos rodean claman venganza.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15802') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15807', `Locale`='esES', `Text0_0`='¡TÚ!$B$B¿Acaso crees que puedes venir aquí y quedarte con mi puesto? ¡Pues te equivocas, $gmuchacho:muchacha;!$B$BHe oído vuestros susurros en el salón. Esos tipos de los que te has rodeado no son más que aduladores lamebotas y no harán nada por ti. Lo sé... Porque yo los contraté a todos.$B$B¡Tu meteórico ascenso al poder ha sido pura suerte y estoy preparado para hacerte caer! Ándate con ojo, $N. ¿Quién sabe cuándo me necesitarás a tu lado?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15807') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15818', `Locale`='esES', `Text0_0`='¡Estoy en la cresta de la ola, colega! Tecnología imbuida con los elementos. Explorando las profundidades de Azeroth en busca de conocimientos perdidos y nuevos recursos.$B$BAcuérdate de mi nombre.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15818') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15819', `Locale`='esES', `Text0_0`='No sé tú, colega, pero yo no puedo respirar bajo el agua.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15819') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15820', `Locale`='esES', `Text0_0`='¿Y perderme la ocasión de ponerme mi maravilloso casco personalizado? Ni hablar.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15820') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15827', `Locale`='esES', `Text0_0`='¡Me alegro de que estés aquí, $gamigo:amiga;!$B$BLos incendios han obligado a la fauna a refugiarse en esta arboleda. No pueden huir más lejos. ¡Debemos proteger a los supervivientes!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15827') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15831', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Hola, cielo! ¿Qué es eso que he oído sobre $gCara:Locko; y tú?', `Text1_0`=NULL, `Text1_1`='Qué buen día para excavar los últimos depósitos conocidos de kaja\'mita del mundo, ¿verdad, $N?', `Text2_0`=NULL, `Text2_1`='¿No te parece curioso que la kaja\'mita nos hiciera listos hace miles de años, pero no parezca surtir efecto alguno sobre nuestros trols?', `Text3_0`=NULL, `Text3_1`='Destilar Kaja\'Cola de la kaja\'mita fue un golpe de puro genio por tu parte, $N. ¡El Príncipe mercante estará preocupado de que le vayas a quitar el título!', `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15831') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15849', `Locale`='esES', `Text0_0`='¡Observa esta devastación, $c! Sangre y cenizas, hasta donde alcanza la vista.$B$BPero no basta para derrotar a la naturaleza. Con un poco de esfuerzo, aún podemos hacer brotar vida de esta tierra arrasada.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15849') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15850', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Pensé que Tótem de Runa era muy imprudente al levantar el campamento en medio de esta devastación, pero no iba a dejar que el archidruida viajara solo.$B$BEstá planeando algo. Algo importante.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15850') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15852', `Locale`='esES', `Text0_0`='¡Mi raptor puede destrozar LO QUE SEA y A QUIEN SEA!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15852') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15853', `Locale`='esES', `Text0_0`='Te digo que Coren Cerveza Temible está loco.$B$B¡No es culpa MÍA que no le hayan invitado a competir en la Fiesta de la cerveza! Pero eso no ha impedido que él y sus colegas del Hierro Negro hayan dado una paliza a mis empleados y se hayan bebido toda mi cerveza.$B$B¡Alguien tiene que poner fin a esto!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15853') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15855', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Hay que detener al Señor de la Escarcha Ahune.$BEn estos mismos instantes está tomando forma al ser invocado de su prisión elemental por los cultores locos del Martillo Crepuscular.$BSi consigue entrar en nuestro mundo por completo, nos enterrará bajo el manto de un invierno perpetuo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15855') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15856', `Locale`='esES', `Text0_0`='...por favor, ayuda...  ...El Jinete decapitado...debe ser detenido...  ...¡Ha matado a tantos! Y, sin embargo, en cuanto sale la luna sigue cabalgando en busca de nuevas víctimas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15856') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15857', `Locale`='esES', `Text0_0`='Alguien debe detener a Químicos La Corona S.L. Apuesto mi cohete abisal chapado en oro a que están fabricando la próxima gran plaga con el dinero de los ciudadanos adictos al chocolate...$B$B¡Ojalá encontrara a alguien que entregue los papeles del juzgado a sus boticarios! Entonces acabaríamos con esto... y ya que estamos, ¡quizá podríamos confiscar sus activos!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15857') AND (`Locale`='esES');
