UPDATE `npc_text_locale` SET `ID`='15953', `Locale`='esES', `Text0_0`='Estamos tras las líneas enemigas, $N. ¡Tenemos que hacer todo el daño que podamos!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15953') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15954', `Locale`='esES', `Text0_0`='Si permitimos al Martillo Crepuscular abrir portales como este por todo Azeroth, no habrá ejército en el mundo que pueda salvar el planeta. ¡Tenemos que cerrar esa red de túneles!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15954') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15955', `Locale`='esES', `Text0_0`='Debemos perseverar, $N. Eso es algo que aprendí de nuestro amado ancestro lobo.$B$BNunca, nunca, nunca te rindas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15955') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15956', `Locale`='esES', `Text0_0`='¡Pyrendius ha muerto! Nuestro trabajo ha concluido.$B$BAtraviesa la puerta de llamas para volver al Monte Hyjal, $c. Yo me aseguraré de que se cierra la puerta.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15956') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15965', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='<Aunque tienes delante de ti el cuerpo físico de Ysera, su consciencia parece alternar entre este mundo y el otro.>', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15965') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15971', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Los druidas de la garfa no estamos hechos para el trabajo en el frente. Somos espías aéreos, infiltrados...$B$BPero a veces me gusta ensuciarme los espolones.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15971') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15979', `Locale`='esES', `Text0_0`='Sierra Espolón siempre ha tenido problemas, $N, pero la tierra y sus gentes nunca han sufrido tanto. La Horda nos destruirá a todos si no le plantamos cara.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15979') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15982', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡No me sacarás nada, $r!', `Text1_0`=NULL, `Text1_1`='<Marion grazna con fuerza y te lanza una mirada furibunda.>', `Text2_0`=NULL, `Text2_1`='Vuestras ciudades caerán, $r. ¡Pereceréis en la oscuridad del nuevo orden!', `Text3_0`=NULL, `Text3_1`='Haz lo que se te antoje. Mis nuevos maestros grabarán mi nombre en tu carne antes de ejecutarte.', `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15982') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15987', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Pidió huevos. Huevos para su criadero ígneo en otro reino.$B$BY un huevo... un huevo especial... lo quería más que cualquier otro.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15987') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15988', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Domina los cielos, pero también la oscuridad. Es del linaje del Guardián de la Tierra. ¡Es invencible!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15988') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15989', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡$R idiota! Ahora existe un nuevo orden. Aviana está muerta. ¡MUERTA! Y nunca volverá.$B$BDíselo a los druidas de los árboles.$B$BPronto Sethria será la señora de los cielos y se ha asegurado de que Aviana no pueda renacer nunca. El huevo es suyo.$B$BLlegas demasiado tarde.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15989') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15995', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡Helado!', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15995') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='15997', `Locale`='esES', `Text0_0`='¡Esa bestia vil ha sido espantada, pero mis preciosas aguas han sido contaminadas! Debo purificarlas inmediatamente.$B$BSi insistís en quedaros aquí, preparaos para defenderos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='15997') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16002', `Locale`='esES', `Text0_0`='Tanto Choluna como Thisalee me han informado. Ya lo veo todo claro...$B$B$N, creo que Aviana volverá a nosotros dentro de poco, ¡pero solo si logramos detener a la dragona negra Sethria!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16002') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16003', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Psst, ¡aquí! Permanece fuera de la vista un momento...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16003') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16007', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Muchos reniegan de la magia de sangre por considerarla un arte oscuro y olvidado. En ese sentido, y en muchos otros, comparte algunas características con la magia demoníaca. Aquí, en los restos arrasados por los demonios de La Ciénaga Negra, creemos que nuestro estudio de la sangre prosperará.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16007') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16015', `Locale`='esES', `Text0_0`='Aquí estamos aún más al descubierto que antes, $r.$B$B¡Debemos encontrar un refugio seguro cuanto antes!', `Text0_1`=NULL, `Text1_0`='Debemos encontrar a tus aliados y descubrir los planes de los nagas para esta región.$B$BPero antes debemos encontrar un lugar más seguro.', `Text1_1`=NULL, `Text2_0`='Tengo la impresión de que no alcanzamos a comprender realmente lo que ocurre con los nagas en este lugar.$B$BAntes nunca los había visto tan organizados ni tan disciplinados.', `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16015') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16019', `Locale`='esES', `Text0_0`='<El agua distorsiona un poco los gemidos de dolor del soldado herido.>', `Text0_1`='<El agua distorsiona un poco los gemidos de dolor del soldado herido.>', `Text1_0`='<Parece que el soldado herido todavía respira. Afortunadamente, su burbuja permanece intacta.>', `Text1_1`='<Parece que el soldado herido todavía respira. Afortunadamente, su burbuja permanece intacta.>', `Text2_0`='<El soldado tiene corte profundo de aspecto doloroso en un costado.>', `Text2_1`='<El soldado tiene corte profundo de aspecto doloroso en un costado.>', `Text3_0`='<Parece que este soldado está fingiendo para no despertar las sospechas de los nagas. Puede que eso le haya salvado la vida.>', `Text3_1`='<Parece que este soldado está fingiendo para no despertar las sospechas de los nagas. Puede que eso le haya salvado la vida.>', `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16019') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16032', `Locale`='esES', `Text0_0`='Parece ser que estamos enfrentándonos a algo mayor que lo que cualquiera del Anillo de Tierra haya podido sospechar.', `Text0_1`=NULL, `Text1_0`='Hay una gran concentración de nagas en el exterior de esa caverna.$B$BHemos tenido mucha suerte de haber podido llegar hasta aquí sin llamar su atención.', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16032') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16033', `Locale`='esES', `Text0_0`='Nuestros soldados... son todos prisioneros.', `Text0_1`=NULL, `Text1_0`='Muchos de nosotros intentamos escapar de los nagas.$B$BY yo soy el único que lo ha conseguido...', `Text1_1`=NULL, `Text2_0`='No sé por qué, pero esos monstruos se esfuerzan por salvarles la vida a nuestros hombres...', `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16033') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16037', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Goldrinn es el ancestro lobo, al que se conoce como \"Lo\'Gosh\" en la lengua de la Horda. Su tenacidad es legendaria.$B$BHay un santuario dedicado a él en una cuenca al oeste del Árbol del Mundo, pero no hemos sabido de sus guardianes desde el ataque. Me temo lo peor.$B$BAntes de que podamos proseguir, debes salvar el santuario, descubrir el paradero del ancestro lobo y rechazar a sus enemigos.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16037') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16038', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Aessina es el espíritu de la naturaleza, mi compañera inmortal tanto en este mundo como en el sueño más allá. Su santuario está en la zona oeste de Hyjal, en la vanguardia de la ofensiva Crepuscular; y en estos momentos puede que esté en llamas.$B$B$N, nunca podremos restaurar el Monte Hyjal sin la ayuda de Aessina. Salva su santuario de las llamas junto con todos los animales de los alrededores que se vean amenazados por el incendio. Debemos hacerlo antes de que todo el monte esté perdido.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16038') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16039', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Aviana es la desaparecida guardiana de los cielos, una diosa pájaro que murió luchando contra las hordas de demonios hace diez mil años. Al suroeste de Nordrassil, entre las ramas de un gran árbol, se alza un santuario construido en su memoria.$B$BLos Druidas de la Garfa tienen su base dentro del santuario y necesitaremos su ayuda. Debes ayudarles. Y ayudar a Aviana si encuentra el camino de vuelta a este mundo...', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16039') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16040', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='He enviado al Archidruida Hamuul Tótem de Runa a las ruinas carbonizadas del Santuario de Malorne. Está detrás de las líneas enemigas, en una tierra carbonizada llamada La Estela Ardiente. Intenta descubrir cómo podremos deshacer el daño e investiga el paradero del ancestro tortuga Tortolla.$B$BPero antes de que vayas a ayudarle, debes asegurarte de que el Santuario de Aessina, al oeste, está a salvo de las llamas. De lo contrario, ¡todo estará perdido! Concéntrate en lo que haya que hacer en la Arboleda de Aessina y después te enviarán con el archidruida a tiempo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16040') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16047', `Locale`='esES', `Text0_0`='Nuestra ciudad está bajo el maldito océano, nuestra fortaleza está llena de traidores Hierro Negro y yo estoy aquí tumbado con una herida tal que no puedo hacer nada para evitar todo eso.$B$B¡Así que te doy la bienvenida a Puerto de Menethil!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16047') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16050', `Locale`='esES', `Text0_0`='Ysera me ha enviado aquí para que establezca contacto con el espíritu de la naturaleza, Aessina.$B$BSin embargo, el ancestro permanece en silencio.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16050') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16051', `Locale`='esES', `Text0_0`='Nosotros no llevamos la guerra a la Horda, fue la Horda la que nos trajo la guerra a nosotros.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16051') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16061', `Locale`='esES', `Text0_0`='Soldado Reginald Polard, Decimotercera Brigada, infantería de Ventormenta, $gseñor:señora;.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16061') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16062', `Locale`='esES', `Text0_0`='Ya los teníamos a la vista... los barcos de la Horda... Y de la nada aparecieron unos tentáculos que atraparon nuestro barco... ¡lo partieron en dos!$B$BLos supervivientes... fuimos capturados por los nagas en cuanto tocamos el agua.$B$BEso fue hace casi una semana.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16062') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16063', `Locale`='esES', `Text0_0`='Fue extraño... Nunca pensé que los nagas se molestarían en mantenernos con vida.$B$BCrearon una especie de burbujas perladas... una por hombre... Las hicieron para que pudiéramos respirar.$B$BTodos los días enviaban a un puñado de prisioneros a... alguna parte.$B$BNunca los volvimos a ver...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16063') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16064', `Locale`='esES', `Text0_0`='Uno de los soldados consiguió liberarse de alguna manera y rompió un puñado de nuestras perlas... nos liberó.$B$BAlgunos nadaron hacia la superficie, otros vimos esta cueva y tratamos de llegar a ella.$B$BYo soy el único que lo consiguió...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16064') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16066', `Locale`='esES', `Text0_0`='Ni rastro... $B$BTraían soldados nuevos todos los días. Tanto de la Horda como de la Alianza.$B$BA algunos se los llevaban el mismo día... otros, como yo, nos quedamos toda una semana... quizá más.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16066') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16067', `Locale`='esES', `Text0_0`='Ni lo intentes, $r.$B$BSal de aquí mientras puedas... $B$BSon demasiados... muerte segura...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16067') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16069', `Locale`='esES', `Text0_0`='La ciudad ha sido derrotada. Los Azsh\'ir claman tu nombre en la victoria. Hoy te has ganado mucho respecto.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16069') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16070', `Locale`='esES', `Text0_0`='No esperaba que tomáramos la ciudad tan rápido. Al parecer tendremos mucho tiempo para prepararnos para la invasión.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16070') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16071', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Los conjuros de invocación están preparados, pero dependíamos del Crisol de Nazsharin para obtener energía para nuestro hechizo. Debemos recuperar el artefacto.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16071') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16075', `Locale`='esES', `Text0_0`='Vida nueva a partir de las cenizas de la vieja... es el regalo de Aessina.$B$BSin ti, este milagro no hubiera sucedido nunca.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16075') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16076', `Locale`='esES', `Text0_0`='<El ancestro se vuelve para mirarte con unos ojos más grandes que tu cabeza. Mirar en ellos es como caer en un océano profundo y azul.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16076') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16078', `Locale`='esES', `Text0_0`='¿Qué... dónde estoy? ¿Eres uno de ellos?', `Text0_1`='¿Qué... dónde estoy? ¿Eres uno de ellos?', `Text1_0`='¿Ya ha terminado todo?', `Text1_1`='¿Ya ha terminado todo?', `Text2_0`='¡Debo... volver con... Troncoferro!', `Text2_1`='¡Debo... volver con... Troncoferro!', `Text3_0`='Tú no eres del Martillo Crepuscular. ¿Qué ocurre ahí fuera?', `Text3_1`='Tú no eres del Martillo Crepuscular. ¿Qué ocurre ahí fuera?', `Text4_0`='Tengo quemaduras. Graves.', `Text4_1`='Tengo quemaduras. Graves.', `Text5_0`='El Martillo Crepuscular... Los he oído correr y gritar... ¿Vamos a contraatacar?', `Text5_1`='El Martillo Crepuscular... Los he oído correr y gritar... ¿Vamos a contraatacar?', `Text6_0`='¡La ciudad... derruida! No pudimos detenerlos.', `Text6_1`='¡La ciudad... derruida! No pudimos detenerlos.', `Text7_0`='Han abierto las puertas que dan a las Tierras de Fuego... ¡Dime que aún hay esperanza!', `Text7_1`='Han abierto las puertas que dan a las Tierras de Fuego... ¡Dime que aún hay esperanza!' WHERE (`ID`='16078') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16084', `Locale`='esES', `Text0_0`='Esa criatura... es bastante anciana y muy sabia. Si pudiéramos hablar con Nespirah aprenderíamos mucho sobre Vashj\'ir y los nagas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16084') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16085', `Locale`='esES', `Text0_0`='Esta vieja posada está hecha polvo, pero sería una buena fortaleza.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16085') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16090', `Locale`='esES', `Text0_0`='No puedo llegar a comprender el propósito o el origen de este lugar.$B$BEsos pasillos se utilizaban para el movimiento de tropas, sí, pero también están llenos de cuerpos torturados y calcinados, algunos parcialmente devorados, o simplemente aplastados y golpeados.$B$BEstamos en las Tierras de Fuego, pero siento como si hubiéramos cruzado el umbral del infierno.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16090') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16095', `Locale`='esES', `Text0_0`='La tenacidad de Goldrinn es legendaria.$B$BGuerreros de todas partes visitan su santuario con la esperanza de recibir parte de esa destreza para el combate.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16095') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16097', `Locale`='esES', `Text0_0`='En toda mi vida, orco, no he visto nada parecido...', `Text0_1`=NULL, `Text1_0`='No estamos adecuadamente equipados para la batalla.$B$BDebemos hacer preparativos...', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16097') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16099', `Locale`='esES', `Text0_0`='Gracias a tus esfuerzos está subiendo la moral, $c.', `Text0_1`=NULL, `Text1_0`='Si encontramos la forma de salir de aquí, Grito Infernal sabrá de tus hazañas, $r.', `Text1_1`=NULL, `Text2_0`='Estos orcos y yo hemos servido juntos en muchas batallas.$B$BNo envidio a ningún naga que se aventure en este lugar.', `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16099') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16100', `Locale`='esES', `Text0_0`='El capitán Troncoferro nunca se rindió, dijo que les haría pagar por cada centímetro de tierra que profanaran. ¡Quizá siga vivo ahí abajo, en alguna parte!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16100') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16101', `Locale`='esES', `Text0_0`='No puedo quejarme de los materiales que hay aquí abajo. Recambios para todo. Recambios de los recambios. ¡Dame unos cuantos días y construiré un bólido acuático con las algas y las conchas!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16101') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16105', `Locale`='esES', `Text0_0`='Este lugar no es tan frío como las ruinas y el aire es un poco húmedo, si quieres saber mi opinión, pero tiene un toque confortable y hogareño.$B$B¿No crees?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16105') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16106', `Locale`='esES', `Text0_0`='¡No sé tú, pero yo sí que echo de menos el ilimitado surtido de materias primas que había bajo las ruinas!$B$B¡No estoy seguro de que ahora pudiera fabricar una simple granada con lo poco que podemos sacar de esta cueva!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16106') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16107', `Locale`='esES', `Text0_0`='Nagas.$B$B<Espinavil escupe en el suelo con disgusto.>$B$BHa sido bastante tonto por su parte creer que podían hacer trabajar a un orco a ritmo de látigo. Haremos que hasta el último de ellos se arrepienta de no habernos matado cuando tuvieron la oportunidad.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16107') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16108', `Locale`='esES', `Text0_0`='Te doy la bienvenida, camarada.  No tenemos mucho que ofrecerte en tema de suministros, pero te ayudaré en todo lo que pueda.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16108') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16109', `Locale`='esES', `Text0_0`='Mi plan de rescate es brillante. Infalible. Tan solo un goblin podría impedirme tener éxito. Un goblin con aletas. Que pensamiento tan terrible.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16109') AND (`Locale`='esES');
