UPDATE `npc_text_locale` SET `ID`='16195', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Por qué hacen esto? ¿Por las perlas? ¿Por la guerra? Ese ancestro, Nespirah, es mucho más poderosa de lo que creen los nagas. Si son capaces de conseguir que ella se pliegue a lo que desean... El daño que podrían causar sería terrible.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16195') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16197', `Locale`='esES', `Text0_0`='Necesitamos tu ayuda, $c. Hemos luchado hasta la extenuación para llegar aquí, pero siguen apareciendo más nagas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16197') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16199', `Locale`='esES', `Text0_0`='Si tienes sangre naga en tus manos, entonces estás de mi parte.$B$BHace mucho tiempo, juré que no volvería ser esclavo de nadie y he mantenido mi promesa hasta que Idra\'kess nos capturó. Ahora apenas puedo contener mi furia. Todo lo que deseo es sangre... No quiero comida, ni dormir, ni siquiera la victoria... solo sangre.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16199') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16203', `Locale`='esES', `Text0_0`='Los elemento\' gritan de ira. No es una coincidencia que los nagas eh\'tén aquí. Eh\'peran algo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16203') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16211', `Locale`='esES', `Text0_0`='¡Ay, ya es difícil mantener el orden por aquí sin que aparezcan de repente todos esos nuevos problemas! ¡Espero que traigas buenas noticias, $N...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16211') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16215', `Locale`='esES', `Text0_0`='Operación: Gnomeregan fue todo un éxito. Es cierto que ese cobarde de Termochufe guardaba un as en la manga, ¡pero le hicimos huir! ¡Es solo cuestión de tiempo que hagamos limpieza en Gnomeregan y la ciudad vuelva a nuestras manos!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16215') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16216', `Locale`='esES', `Text0_0`='Cocinar para el rey es un honor, no un trabajo. ¿Tienes tiempo para ayudarme con unos cuantos recados?', `Text0_1`='Cocinar para el rey es un honor, no un trabajo. ¿Tienes tiempo para ayudarme con unos cuantos recados?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16216') AND (`Locale`='esES');
