UPDATE `npc_text_locale` SET `ID`='16244', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Entre tú y yo, cielo, soy demasiado baja para llevar a cabo este tipo de misiones. Un mes más y me largo.$B$BYa he muerto una vez. No tiene sentido hacerlo una segunda.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16244') AND (`Locale`='esES');
