UPDATE `npc_text_locale` SET `ID`='16258', `Locale`='esES', `Text0_0`='Dar de comer a un ejército de la Horda no es tarea fácil. Tengo montones de cosas que hacer. ¿Tienes tiempo para echarme una mano?', `Text0_1`='Dar de comer a un ejército de la Horda no es tarea fácil. Tengo montones de cosas que hacer. ¿Tienes tiempo para echarme una mano?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16258') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16259', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Debemos hacer lo que sea para recombinar los fragmentos del Pilar del Mundo.$B$BPero antes tenemos que recuperarlos todos.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16259') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16260', `Locale`='esES', `Text0_0`='Llevo toda la vida en los Páramos de Poniente, $ghermano:hermana;. Recuerdo los días en los que los Páramos de Poniente eran la tierra más exuberante a este lado de la Vega de Tuercespina. Aquellos días desaparecieron hace tiempo.$B$BAunque ya no tenemos a la Hermandad Defias causando problemas como antes, las consecuencias de su reino de terror todavía son patentes. Para empeorar las cosas aún más, estamos sufriendo la peor crisis económica de toda nuestra historia. Todo el oro que gastamos luchando contra la Plaga hizo que los que no estuvieran ocupados en algo relacionado con el ejército se quedaran sin trabajo y, al final, sin casa.$B$B¿Adivinas dónde terminó toda esa gente?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16260') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16262', `Locale`='esES', `Text0_0`='Sí, claro, decía ella, no será más que un viajecito a los baldíos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16262') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16270', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='En mi juventud, a mi familia no le sentó bien que tuviera interés en el chamanismo. Yo era tonta y descarada y me escapé de casa para aprender por mi cuenta. Cuando un día regresé a mi casa, vi que mis padres ya no estaban. Mi casa estaba destrozada. Nunca encontraron a los culpables.$BMe he pasado toda la vida esforzándome por ampliar mi visión chamanística para ver el pasado; para averiguar qué le ocurrió a mi familia y quién es el responsable. He avanzado mucho. He aprendido a concentrarme en hechos impactantes del pasado reciente, pero aún me queda mucho por aprender si alguna vez espero poder ver algo que ocurrió hace tanto tiempo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16270') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16275', `Locale`='esES', `Text0_0`='La guerra de Rasganorte nos pasa factura, $N. Mientras salíamos victoriosos en los baldíos helados, nuestras tierras caían en la confusión y el desorden.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16275') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16283', `Locale`='esES', `Text0_0`='Deja a este Faucedraco que disfrute de su bebida, $c.$B$BHe luchado contra criaturas que te producirían escalofríos y he visto a mi gente masacrada en los pantanos de Los Humedales y entre las ruinas de Terrallende.$B$BPiérdete.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16283') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16288', `Locale`='esES', `Text0_0`='¿Qué haces aquí, $c?$B$B¿No deberías estar ya en las Tierras Altas Crepusculares arrasando a ese maldito culto?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16288') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16303', `Locale`='esES', `Text0_0`='No hay nada caótico en eh\'te ataque. Los elementales y los gigantes se han organizado para atacah\'nos. De alguna manera nos hemos granjeado la ira de Therazane, la Madre Pétrea en persona.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16303') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16304', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='La sacerdotisa sabe mucho sobre Zul\'Gurub.$B$BLa sacerdotisa cree que puedes necesitar su ayuda.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16304') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16348', `Locale`='esES', `Text0_0`='En el culto Crepuscular hay magos que nos amenazan.$B$BEstamos deseando borrarlos de la faz de la tierra y eliminarlos de nuestro hogar.$B$BEstamos divididos, por lo que no podemos.$B$BAsí que me dedico a observar... $B$BY a esperar...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16348') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16365', `Locale`='esES', `Text0_0`='Infralar era un lugar tranquilo de ritmo lento. Siempre vivo. Siempre en movimiento. Pero sin prisas. En aquella época, yo deseaba llegar a ser uno con la tierra, y en gran medida, lo conseguí.$B$BMás tarde, el Guardián de la Tierra regresó, herido. Al principio no trastocó el equilibrio. Ya había venido aquí en otras ocasiones y le conocíamos. El cambio no comenzó de verdad hasta que el culto Crepuscular se unió a él.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16365') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16366', `Locale`='esES', `Text0_0`='Cuando el Guardián de la Tierra se recuperó, empezó a molestarle la tranquilidad. La paz. Y no pasó mucho tiempo hasta que volvió a los dragones de piedra contra nosotros.$B$BSin Alamuerte, esos dragones ya no tienen un propósito. Podemos recordarles nuestra fuerza. Devolverlos a la Madre Tierra.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16366') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16373', `Locale`='esES', `Text0_0`='¡Cómo se atreven a hacer prisionero a nuestro Rey!$B$B¡Recuperaremos el Hogar Pétreo y entonces avanzaremos por el frente de batalla para aplastar a los troggs de piedra en su hogar!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16373') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16374', `Locale`='esES', `Text0_0`='¡Salgamos de aquí y hagamos que esos troggs de piedra vuelvan a la tierra, $r!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16374') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16375', `Locale`='esES', `Text0_0`='Te necesito en el frente, $N.$B$BTú marcarás la diferencia. Aplastaremos a los troggs de piedra de una vez por todas.$B$B¡Debemos rescatar al rey Mantomena!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16375') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16376', `Locale`='esES', `Text0_0`='<Berilo se queja y sonríe con desdén.>$B$B¡Malditos niños de hoy en día!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16376') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16383', `Locale`='esES', `Text0_0`='La entrada de Alamuerte en nuestro mundo abrió una fisura entre este e Infralar.$B$BLa única forma de estabilizar la fisura es volver a construir el Pilar del Mundo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16383') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16390', `Locale`='esES', `Text0_0`='Ya estoy cansado de estar aquí sentado. Espero que el desgraciado chupatintas que esté impidiendo el regreso de Danath reciba un puñetazo en los huevillos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16390') AND (`Locale`='esES');
