UPDATE `npc_text_locale` SET `ID`='16412', `Locale`='esES', `Text0_0`='Tanta pérdida sin sentido.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16412') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16413', `Locale`='esES', `Text0_0`='La pesca corre en las venas de mi familia. Sin embargo, tú sí parece que necesites ayuda. ¿Qué te parece hacer unos recados para un Maestro pescador?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16413') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16422', `Locale`='esES', `Text0_0`='Hemos venido para intentar ayudar, pero al parecer que tú les caigas bien no significa que nosotros también.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16422') AND (`Locale`='esES');
