UPDATE `npc_text_locale` SET `ID`='16712', `Locale`='esES', `Text0_0`='Saludos, joven. Me alegro de volver a ver un alma noble.$B$BLa Masacre, antes Eldre\'thalas, está corrupta hasta el tuétano. He visto a mis hermanos sucumbir ante las fuerzas del caos y la oscuridad. Muchos arrasarían este lugar por completo para eliminar sus horrores para siempre.$B$BPero La Masacre se merece la salvación, mortal. Ayúdame, y quizá entre los dos podamos desatar el inmenso poder que se perdió aquí para las fuerzas del bien, como sucedía hace tanto tiempo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16712') AND (`Locale`='esES');
