UPDATE `npc_text_locale` SET `ID`='16743', `Locale`='esES', `Text0_0`='No escuches nada de lo que diga Alexi. Fue un idiota al quedarse durante la peste, y ahora míralo: no es mejor que las demás abominaciones del lugar.$B$BNo te preocupes, recuperaremos Scholomance y las tierras circundantes para el último hijo viviente de Barov: su heredero legítimo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16743') AND (`Locale`='esES');
