UPDATE `npc_text_locale` SET `ID`='16759', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='A ver... He oído que el magus Bisp está pasando mucho tiempo encerrado en su torre, trabajando en un hechizo nuevo o algo así.$B$BHe oído que Del Gahrron estaba pensando en jubilarse y volver a su granja. De hecho, a lo mejor ya se ha ido.$B$BAh, y me han contado que hay $gun:una; $r $c husmeando por la ciudad y haciéndole a la gente toda clase de preguntas aburridas.$B$BSolo era una broma, cielo. Espero que la información te sea de ayuda.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16759') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16760', `Locale`='esES', `Text0_0`='Que la Luz te ampare, hijo. ¿Qué puedo hacer por ti en este día tan estupendo?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16760') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16761', `Locale`='esES', `Text0_0`='¿Suministros robados? Déjame pensar...$B$BSé que una sacerdotisa llamada Daria se llevó todo el vino después del servicio, pero no lo tiró tal y como se lo pedí. Pero creo que pedirá disculpas muy pronto.$B$B<Alphus te guiña el ojo.>$B$BTambién he escuchado una cantidad extraordinaria de confesiones de uno de los magos... ¿Cómo se llamaba? ¿Crisp? ¿Lisp? Algo así.$B$BYa puedes seguir con lo tuyo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16761') AND (`Locale`='esES');
