UPDATE `npc_text_locale` SET `ID`='16938', `Locale`='esES', `Text0_0`='Alamuerte ha regresado. Ha tomado Grim Batol y gran parte de las Tierras Altas, y me veo prácticamente impotente para detenerlo.$B$BPronto llegará nuestra reina, y juntos pondremos al antiguo Guardián de la Tierra en su sitio.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16938') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16939', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Llevo mucho tiempo observando a los Hierro Negro... Maquinando, esperando, deseando tener la oportunidad de expulsarlos y recuperar mi tierra.$B$BPueden quedarse con la Montaña Roca Negra. Espero que vuelva a entrar en erupción y se los lleve a todos. Quiero lo que ahora se llaman Las Estepas Ardientes, mi parte de las Montañas Crestagrana que destruyó su señor ígneo.$B$B$C, voy a enseñarle al mundo el potencial que aún le queda a mi tierra. Levantaré un reino de paz y justicia sobre la muerte y el engaño que dejan tras de sí. Mi vigilia ha terminado. He venido para luchar por lo que me pertenece por derecho.$B$BY estoy dispuesta a morir por ello.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16939') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16943', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='El fortín está fortificado, pero nuestros enemigos se acercan por todas partes. Lo más inteligente sería asegurarnos de controlar la costa antes de avanzar tierra adentro.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16943') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16945', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Parece que han encontrado el camino de vuelta a casa. Qué alivio.$B$B¡Y tú también has vuelto! ¿Te interesa unirte a nosotros?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16945') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16946', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Algún día me gustaría establecerme en la Capilla de la Esperanza de la Luz. No soy buena luchadora, pero eso no quiere decir que no pueda contribuir a la causa.', `Text1_0`=NULL, `Text1_1`='Hace poco que los chicos y yo viajamos juntos, pero ya me cuesta imaginar la vida sin cualquiera de ellos.', `Text2_0`=NULL, `Text2_1`='Espero que estés usando los objetos de la caravana. Si no los estamos utilizando nosotros, puedes tomarlos prestados.', `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16946') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='16947', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡¿Por qué se ha ido así como así?!', `Text1_0`=NULL, `Text1_1`='Prométeme que lo encontrarás, $N.', `Text2_0`=NULL, `Text2_1`='<Fiona está bastante alterada. No parece estar de humor para hablar.>', `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16947') AND (`Locale`='esES');
