UPDATE `npc_text_locale` SET `ID`='16956', `Locale`='esES', `Text0_0`='Oh, ah... ¡Hola, $gbuen viajero:buena viajera;! ¿Qué puede hacer este humilde goblin por ti?$B$BTe aseguro que me encuentro en la Capilla de la Esperanza de la Luz exclusivamente por mi odio hacia la Plaga, que comparto con la Cruzada Argenta, ¡rotundamente no porque me esté escondiendo de un vergonzoso accidente de zepelín!$B$BAhora voy a dejar de hablar, antes de incriminarme más.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='16956') AND (`Locale`='esES');
