UPDATE `npc_text_locale` SET `ID`='17108', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='<Una visión onírica de Ysera flota frente a ti; su atención está dividida. Tras un momento, se dirige a ti.>$B$BDurante muchos años, Aviana revoloteó en la periferia de mis sueños. Me preguntaba si alguna vez volvería con nosotros. Temo que su espíritu se haya oscurecido con el paso del tiempo...$B$BPero tenemos problemas más inmediatos si queremos salvar este monte.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17108') AND (`Locale`='esES');
