UPDATE `npc_text_locale` SET `ID`='17142', `Locale`='esES', `Text0_0`='Si no has venido a trabajar, te ruego que abandones la excavación.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17142') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17143', `Locale`='esES', `Text0_0`='Hola, tío bueno.', `Text0_1`='Hola, tío bueno.', `Text1_0`='¿Te sobra sitio, cielo?', `Text1_1`='¿Te sobra sitio, cielo?', `Text2_0`='¿Vamos en la misma dirección?', `Text2_1`='¿Vamos en la misma dirección?', `Text3_0`='¿Hay sitio para una señorita?', `Text3_1`='¿Hay sitio para una señorita?', `Text4_0`='Hoy estás muy apuesto, Martek.', `Text4_1`='Hoy estás muy apuesto, Martek.', `Text5_0`='Ooh, Martek, ¿puedo ir contigo?', `Text5_1`='Ooh, Martek, ¿puedo ir contigo?', `Text6_0`='Martek, eres el orco más valiente que conozco.', `Text6_1`='Martek, eres el orco más valiente que conozco.', `Text7_0`='¡Sálvame, Martek! ¡Te necesito!', `Text7_1`='¡Sálvame, Martek! ¡Te necesito!' WHERE (`ID`='17143') AND (`Locale`='esES');
