UPDATE `npc_text_locale` SET `ID`='17150', `Locale`='esES', `Text0_0`='Admito que siento curiosidad por este pequeño emprendedor.$B$BOdio los prejuicios, no te creas, pero he oído algunos rumores inquietantes.$B$BEl tiempo dirá si sus intenciones son justas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17150') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17151', `Locale`='esES', `Text0_0`='¿Qué te trae a la compañía de unos viejos e inútiles perros del desierto como nosotros?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17151') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17152', `Locale`='esES', `Text0_0`='No me sorprende.$B$BNo puedo pasar por alto tantas preguntas...$B$B¿Por qué tantas armas en una excavación arqueológica? ¿Qué se hace con las reliquias que se desentierran? ¿Con qué autoridad se está llevando a cabo el trabajo?$B$BLas respuestas no llegan. Supongo que tendré que descubrirlas por mí mismo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17152') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17155', `Locale`='esES', `Text0_0`='¡Voto a bríos! ¡Brindemos, amigo!$B$BAdmira las vistas.$B$BToda esta región ha estado oculta durante siglos, ¿sabes?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17155') AND (`Locale`='esES');
