UPDATE `npc_text_locale` SET `ID`='17160', `Locale`='esES', `Text0_0`='Creo que sé a qué te refieres...$B$BLa verdad, solo estoy aquí porque quiero expandir mis horizontes, ver mundo y esas cosas.$B$BNo me preocupan mucho los asuntos de los demás.$B$BPero me alegro de conocerte.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17160') AND (`Locale`='esES');
