UPDATE `npc_text_locale` SET `ID`='17168', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Boca del Dragón se estableció para darle a la Alianza su primera base de verdad en las Tierras Inhóspitas. A pesar de su emplazamiento entre Ventormenta y Dun Morogh, tradicionalmente las Tierras Inhóspitas han sido territorio de la Horda.$B$BSi queremos que eso cambie algún día, tenemos que empezar a hacernos respetar. Huelga decir que los ataques de los dragones contra Nuevo Kargath no le vienen mal a nuestra causa.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17168') AND (`Locale`='esES');
