UPDATE `npc_text_locale` SET `ID`='17397', `Locale`='esES', `Text0_0`='¡Malditos sean los Faucedraco!$B$BMatan a nuestros pájaros, atacan a nuestras familias, toman nuestras tierras. Incluso mientras el mundo se derrumba a nuestro alrededor ellos siguen asaltando nuestras casas.$B$BSi tu Alianza quiere la ayuda de los Martillo Salvaje, solo hay una forma de convencernos. Detened. A. Los. Faucedraco.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17397') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17398', `Locale`='esES', `Text0_0`='<Blundy mira por encima de sus copas.>$B$BSí, es cierto. El Alto chamán está fuera, en el campo de batalla, luchando por reconquistar las Ruinas de Bramal. Yo soy lo que se llama un \"chamán inferior\".$B$BMientras él esté fuera, me ha dejado aquí para que me ocupe de todos los asuntos chamanísticos de Bramal...', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17398') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17401', `Locale`='esES', `Text0_0`='¡Saludos, $c! Soy de la unidad de exploración avanzada de Bancalto y estoy intentando ganarme a los Martillo Salvaje aquí, en Bramal.$B$BA los jinetes de grifos les está costando mucho acabar con esos jinetes de dracos Faucedraco. Tengo un plano para un artilugio que podrá ayudar, pero necesito una serie de suministros. Un montón.$B$B¿Puedes ayudarme?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17401') AND (`Locale`='esES');
