UPDATE `npc_text_locale` SET `ID`='17513', `Locale`='esES', `Text0_0`='¡Hola, camarada!$B$BMe llamo Russell y la música es lo mío. Todos los héroes Martillo Salvaje de épocas pasadas siguen vivos en las canciones. A través de mi laúd y el sonido de mi voz, nuestros guerreros alcanzan la inmortalidad.$B$B¡Quédate un rato y escucha!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17513') AND (`Locale`='esES');
