UPDATE `npc_text_locale` SET `ID`='17516', `Locale`='esES', `Text0_0`='¡Empecemos! El primer verso trata sobre nuestra querida Fanny, pero no acabo de conseguir que me salga.$B$B¿Qué rima con \"ojos\"?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17516') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17517', `Locale`='esES', `Text0_0`='¡Ah, ya veo qué tipo de $c eres, $N! Ahora, el segundo verso trata de Fanny y Barbafuego que terminan juntos.$B$B¿Qué rima con \"profesar\"?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17517') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17518', `Locale`='esES', `Text0_0`='... Mmmm, vale, creo que puedo hacer que funcione. ¡El último verso! ¡Una enardecedora celebración del amor a la vez que llama a los Martillo Salvaje a la acción!$B$BNecesito una rima para \"salvaje...\"', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17518') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17521', `Locale`='esES', `Text0_0`='Cuidado $gmuhacho:muchacha;. Entre los huargos de ahí abajo y los orcos que acechan en el paso, $gun:una; $c podría acabar bien $gmuerto:muerta;.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17521') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17533', `Locale`='esES', `Text0_0`='¡Te doy la bienvenida, $gamigo:amiga; de los Martillo Salvaje!$B$BKirthaven es el centro espiritual de nuestro pueblo, el lugar donde los diferentes clanes pueden reunirse en paz.$B$BEs donde honramos a los héroes muertos antes de enterrarlos con sus grifos en lo alto del Monte del Rayo. Allí pueden pasar la eternidad en el cielo.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17533') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17534', `Locale`='esES', `Text0_0`='Una boda, ¿eh? ¡Si eso no hace que estos se unan me comeré mi martillo!$B$BTenemos que hacer que esta boda sea espectacular, $N, y para eso necesitaremos tu ayuda. Hay un viejo dicho de los Martillo Salvaje... déjame pensar... ¿Cómo era?', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17534') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17537', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Eh. ¿Qué tal te va, $gjefe:jefa;? Hobart y yo estamos trabajando en algunas cosas que te asegurarán un buen suministro de productos fenomenales para cuando se termine la kaja\'mita.$B$BMe temo que no queda mucho en las minas, y cuando se acabe, la Kaja\'Cola desaparecerá con ella.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17537') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17538', `Locale`='esES', `Text0_0`='¡Me alegro de verte, $N!$B$BSolo quiero asegurarme de que sabes apreciar todos los productos que mi ingenio ha proporcionado a la sociedad comercial de Kajaro.$B$BEsto incluye, la Kaja\'Cola, el Pollotizador, la Ciudad de Bolsillo, el almete de estupidez de cateto ingenioso, el Trenatrón Deluxe SCK, los rayos goblins alucinantes, los ponis flotadores que nunca se desinflan, los Grandes, la Guerreromática NX-01 y los gilgoblins. Aunque esto último no fue un gran éxito. Siguiendo el consejo de mi abogado, afirmo que no tuve nada que ver con ellos.$B$BSea como sea, estoy trabajando en algunas cosas más que creo que podrían interesarte, como el micromecapollo y algo que yo llamo el Sujeto Nueve.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17538') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17550', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¡¿Un cateto?! Yo no hablo con brutos. ¡Cómo te atreves!', `Text1_0`=NULL, `Text1_1`='¿Cómo os llamamos hoy en día? ¿Brutos? ¿Hobgolins?$B$BNo puedo soportarlo. Largo de aquí.', `Text2_0`=NULL, `Text2_1`='Eres grande y fuerte. Pero necesitas darte un baño. Salta a la piscina.', `Text3_0`=NULL, `Text3_1`='¡Oh, no! ¡Te has atrevido a hablar conmigo!', `Text4_0`=NULL, `Text4_1`='¿A qué huele por aquí? Oh, eres tú.$B$B¡Lárgate!', `Text5_0`=NULL, `Text5_1`='¿Cómo te llamas?$B$BSupongo que está bien que los brutos no podáis hablar. De lo contrario, tendría que contarle al Príncipe mercante que me habías hecho enfadar.$B$B¡Ahora fuera!', `Text6_0`=NULL, `Text6_1`='¡Tócame una vez más y te aseguro que haré que te envíen a Minahonda!', `Text7_0`=NULL, `Text7_1`='Oh, ¡eres repugnante! ¡¿Por qué será que siempre son los feos los que intentan echarme la caña?!' WHERE (`ID`='17550') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17552', `Locale`='esES', `Text0_0`='<Para tu sorpresa, la prole de raptor es capaz de comunicarse contigo.>$B$B$G¿Papá:Mamá;...?', `Text0_1`=NULL, `Text1_0`='<Para tu sorpresa, la prole de raptor es capaz de comunicarse contigo.>$B$BTengo hambre... luego existo.', `Text1_1`=NULL, `Text2_0`='<Para tu sorpresa, la prole de raptor es capaz de comunicarse contigo.>$B$B¿Qué significa \"$c\"? ¿Eres $gel patriarca:la matriarca; goblin?', `Text2_1`=NULL, `Text3_0`='<Para tu sorpresa, la prole de raptor es capaz de comunicarse contigo.>$B$BTe llaman $N. ¿Es tu único significante o es un título honorario?', `Text3_1`=NULL, `Text4_0`='<Para tu sorpresa, la prole de raptor es capaz de comunicarse contigo.>$B$BSaludos. He emergido recientemente de un huevo. ¿Y tú?', `Text4_1`=NULL, `Text5_0`='<Para tu sorpresa, la prole de raptor es capaz de comunicarse contigo.>$B$B¿Es cierto que cocináis la carne antes de comérosla? Curioso.', `Text5_1`=NULL, `Text6_0`='<Para tu sorpresa, la prole de raptor es capaz de comunicarse contigo.>$B$BNo llevas yelmo. ¿Eso disminuye tus capacidades intelectuales?', `Text6_1`=NULL, `Text7_0`='<Para tu sorpresa, la prole de raptor es capaz de comunicarse contigo.>$B$BTus dedos son torpes y blandos en sus extremos. ¿Por eso tienes que llevar otros complementos para utilizarlos en combate?$B$BQué interesante.', `Text7_1`=NULL WHERE (`ID`='17552') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17557', `Locale`='esES', `Text0_0`='Desde que era un niño mi padre me ha enseñado a respetar los elementos. La tierra, el fuego, el viento y el mar... Poseen un gran poder, $c.$B$B<Grundy tiene un brillo en los ojos.>$B$BMás del que te imaginas.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17557') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17559', `Locale`='esES', `Text0_0`='¡Ach! $B$BYa era hora de que aparecieras.$B$BLlevo días esperándote.$B$BDejar solo a un viejo enano para que se achicharre... He tenido aprendices mucho mejores que tú.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17559') AND (`Locale`='esES');
