UPDATE `npc_text_locale` SET `ID`='17880', `Locale`='esES', `Text0_0`='Este teletransporte te llevará a Grom\'gol, en el Norte de la Vega de Tuercespina.$B$BNormalmente.$B$BLo que quiero decir es que, a veces, te desintegrará o reducirá todo tu tejido blando a una especie de gelatina verde de hedor insoportable. Pero, en general, ¡es bastante seguro!$B$B... en general.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17880') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17941', `Locale`='esES', `Text0_0`='¡Juguetes hechos a mano de todo Azeroth!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17941') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17950', `Locale`='esES', `Text0_0`='No deberías andar por aquí, colega. Los eh\'píritus eh\'tán inquietos.$BQuizá si sabes algo sobre ah\'tefactos antiguos puedas encontrar la forma de tranquilizah\'los.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17950') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17952', `Locale`='esES', `Text0_0`='Aunque el artefacto parece estar en una condición impecable, no percibes nada de particular en él.', `Text0_1`=NULL, `Text1_0`='El artefacto parece estar intacto, aunque está cubierto totalmente por tierra y barro, y es difícil saberlo con seguridad. No da la impresión de que exista algo inherentemente mágico en él.', `Text1_1`=NULL, `Text2_0`='El artefacto ha superado muchas tormentas y muestra claros signos de deterioro. Resulta improbable que permanezcan en él los poderes que hubiera podido contener antiguamente.', `Text2_1`=NULL, `Text3_0`='Algunas hojas y ramas se aferran a este objeto, lo que indica que probablemente se haya utilizado como parte del nido de alguna criatura. Por lo demás, no hay nada más digno de destacar.', `Text3_1`=NULL, `Text4_0`='Unas extrañas tallas adornan este objeto, quizá hechas por trols en un intento de descifrar sus orígenes. No parece que tuvieran mucho éxito, ya que el objeto permanece inerte.', `Text4_1`=NULL, `Text5_0`='Unas misteriosas runas adornan este artefacto, aunque actualmente parece que no tiene poder alguno.', `Text5_1`=NULL, `Text6_0`='Este objeto es de baja calidad y, probablemente, sea uno de esos artefactos falsos que abundan en el mercado negro.', `Text6_1`=NULL, `Text7_0`='Se pueden ver en este objeto varias manchas de sangre seca, probablemente como resultado de alguna pelea por quitarles el artefacto a los trols. Sin embargo, el objeto es bastante mundano y puede que no mereciera la pena verter sangre por él.', `Text7_1`=NULL WHERE (`ID`='17952') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17960', `Locale`='esES', `Text0_0`='Saludos, $c. Me llamo Thork y estoy al mando de las defensas de este lugar en nombre del Jefe de Guerra.$B$BVeo que has tenido la oportunidad de echar una mano en El Cruce. ¡Continúa así por la Horda!', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17960') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17976', `Locale`='esES', `Text0_0`='$N, me alegro de verte. ¿Has venido para hacer avanzar el frente para nosotras?', `Text0_1`='$N, me alegro de verte. ¿Has venido para hacer avanzar el frente para nosotras?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17976') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17977', `Locale`='esES', `Text0_0`='Las fuerzas elementales no se agotan. Me temo que no podremos aguantar la posición en esta brecha eternamente. Desde luego, no sin ayuda.', `Text0_1`='Las fuerzas elementales no se agotan. Me temo que no podremos aguantar la posición en esta brecha eternamente. Desde luego, no sin ayuda.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17977') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17986', `Locale`='esES', `Text0_0`='Es un hijo de Beth\'tilac, la Viuda Roja. Su prole buscará venganza por la muerte de sus hermanos.', `Text0_1`=NULL, `Text1_0`='Estamos en el corazón del lugar de cría de Beth\'tilac. Más allá de esos pilares que hay al norte, su linaje aguarda. Defenderán a los más jóvenes sin descanso.', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17986') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17990', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Saludos, $c. El Señor del Fuego ha corrompido a nuestros antiguos hermanos y hermanas hasta convertirlos en encarnaciones de furia de magma. Mientras hablamos, los Druidas de la Llama siguen adelante con su plan de destruir el Árbol del Mundo. No permitiremos que eso ocurra.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17990') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='17999', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='¿Has venido pa\' ayudar a los Lanza Negra, $c?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='17999') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18000', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Soy una emisaria de los trols Lanza Negra. La amenaza a la que nos enfrentamos también lo es pa\' la Alianza. ¿Quieres ayudar a los Lanza Negra, $r?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18000') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18024', `Locale`='esES', `Text0_0`='Estoy aquí para asegurarme de que el trabajo se hace a tiempo.$B$B¡Bahía del Botín todavía se está recuperando del maremoto!$B$BLo último que necesitamos ahora es un ataque de una horda de trols que lo arrasan todo, trols zombis, serpientes venenosas y panteras locas.', `Text0_1`=NULL, `Text1_0`='Estoy aquí en representación de los intereses de Bahía del Botín en esta expedición.$B$BSi las cosas van mal, Bahía del Botín tendrá problemas.$B$BTodavía estamos recuperándonos de los acontecimientos del Cataclismo.', `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18024') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18026', `Locale`='esES', `Text0_0`='¡Aquí hace más calor que dentro de un horno! Puf, podría freírme un huevo en la frente.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18026') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18029', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Ese Malfurion... Es tan propio de él mandar a su esbirro $r para hablar conmigo.$B$BHa demostrado ser un cobarde, como siempre.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18029') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18030', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No importa, $r. Los detalles de mi pasado ardieron cuando me uní a los Druidas de la Llama. No son asunto tuyo.$B$BAdemás, he venido para hablar del futuro de este reino. Y no tengo intención de hablarlo contigo.$B$BAhora, dime... ¿Dónde está Malfurion?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18030') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18031', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Muy bien. Malfurion se adentrará en las Tierras de Fuego y nos encargaremos de él allí. Su juicio puede esperar un par de días más.$B$B<Los ojos de Leyara brillan.>$B$BSin embargo, el archidruida tauren no tendrá tanta suerte.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18031') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18032', `Locale`='esES', `Text0_0`='Nuestro asalto a las Tierras de Fuego no puede esperar. Atacaremos a Ragnaros y a Fandral en cuanto podamos.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18032') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18037', `Locale`='esES', `Text0_0`='Acaban de aparecer estos trols en busca de ayuda.', `Text0_1`=NULL, `Text1_0`='Apenas puedo entender lo que dicen estos trols.', `Text1_1`=NULL, `Text2_0`='¿Por qué estos trols no solucionan sus problemas ellos mismos?', `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18037') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18038', `Locale`='esES', `Text0_0`='<El trol canta para sí mismo mientras observa la masacre que le rodea.>', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18038') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18042', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Me alegro de que lo consiguieras, $N.', `Text1_0`=NULL, `Text1_1`='Te doy la bienvenida a La Corriente de Magma, $N.', `Text2_0`=NULL, `Text2_1`='Los despiertallamas de ahí delante son un asunto serio. Ten cuidado.', `Text3_0`=NULL, `Text3_1`='Tendremos un montón de corrientes térmicas ahí delante, $N. No dudes en usarlas si te ves en un aprieto.', `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18042') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18113', `Locale`='esES', `Text0_0`='Hoy hay algo extraño en los vientos. Algo muy extraño.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18113') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18124', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No te preocupes. Mantendré al azotador carmesí a salvo conmigo. Hasta que sepamos más sobre su comportamiento, me temo que tendrá que quedarse aquí, en las Tierras de Fuego.', `Text1_0`=NULL, `Text1_1`='¡Un espécimen único! ¡Se abren tantas posibilidades! Debo dedicarme por entero y con más empeño al estudio de la fauna y flora local.', `Text2_0`=NULL, `Text2_1`='Mientras esté enfrascada en la investigación será asunto tuyo enseñar al azotador cómo funciona el mundo.', `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18124') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18138', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='No podemos descansar todavía, joven. Hyjal todavía resiste, pero si flaqueamos en el umbral del Señor del Fuego, todo estará perdido. Que la fuerza de los Ancestros esté con nosotros.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18138') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18139', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='A nuestros cuervos de tormenta les cuesta volar en este aire con una densidad tan alta, pero los halcones de fuego de esa cresta planean con facilidad.$B$BSon tan salvajes como las criaturas del plano del Fuego, sin embargo, hay belleza en sus formas.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18139') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18141', `Locale`='esES', `Text0_0`='Apenas podemos mantenernos en nuestra posición. Es necesario que todos los soldados que estén en condiciones de luchar se presenten al servicio.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18141') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18149', `Locale`='esES', `Text0_0`='Vientos de Aviana, otorgadme la fuerza para mantener las llamas a raya.', `Text0_1`=NULL, `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18149') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18150', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Te doy la bienvenida, $N. ¿Has venido a unirte a la invasión de las Tierras de Fuego?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18150') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18151', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Mientras nos preparamos para invadir las Tierras de Fuego, el Señor del Fuego ha enviado a sus esbirros para repeler nuestro ataque. Nuestra posición no debe caer. Nosotros somos lo único que hay entre ellos y el Árbol del Mundo.', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18151') AND (`Locale`='esES');
UPDATE `npc_text_locale` SET `ID`='18152', `Locale`='esES', `Text0_0`=NULL, `Text0_1`='Todavía no tenemos las tropas necesarias para avanzar en las Tierras de Fuego. Primero, debemos recuperar las fuerzas y conseguir suministros. ¿Nos echarás una mano?', `Text1_0`=NULL, `Text1_1`=NULL, `Text2_0`=NULL, `Text2_1`=NULL, `Text3_0`=NULL, `Text3_1`=NULL, `Text4_0`=NULL, `Text4_1`=NULL, `Text5_0`=NULL, `Text5_1`=NULL, `Text6_0`=NULL, `Text6_1`=NULL, `Text7_0`=NULL, `Text7_1`=NULL WHERE (`ID`='18152') AND (`Locale`='esES');
